CREATE PROCEDURE DBO.USR_USP_DATAFORMTEMPLATE_ADD_UMHSBATCH ( @ID uniqueidentifier = null output ,@CHANGEAGENTID uniqueidentifier = null ,@BATCHCONSTITUENTID uniqueidentifier = null ,@BATCHID uniqueidentifier ,@SEQUENCE int = null ,@CONSTITUENTID uniqueidentifier = null ,@MRN nvarchar(50) = N'' ,@CPISEQUENCE nvarchar(50) = N'' ,@CONSTITUENTLOOKUPID nvarchar(200) = N'' ,@FIRSTNAME nvarchar(50) = N'' ,@MIDDLENAME nvarchar(50) = N'' ,@LASTNAME nvarchar(100) = N'' ,@SUFFIXCODEID uniqueidentifier = null ,@BIRTHDATE udt_fuzzydate = '00000000' ,@ADDRESSBLOCK nvarchar(500) = N'' ,@CITY nvarchar(100) = N'' ,@STATEID uniqueidentifier = null ,@POSTCODE nvarchar(24) = N'' ,@COUNTRYID uniqueidentifier = null ,@ADDRESSTYPECODEID uniqueidentifier = null ,@INFOSOURCECODEID uniqueidentifier = null ,@HISTORICALSTARTDATE date = null ,@PHONENUMBER nvarchar(200) = N'' ,@PHONETYPECODEID uniqueidentifier = null ,@PHONE_COUNTRYID uniqueidentifier = null ,@PHONE_OTHER nvarchar(200) = N'' ,@PHONE_OTHERTYPECODEID uniqueidentifier = null ,@PHONE_OTHER_COUNTRYID uniqueidentifier = null ,@PHONESTARTDATE date = null ,@EMAILADDRESS nvarchar(100) = '' ,@EMAILADDRESSTYPECODEID uniqueidentifier = null ,@EMAILADDRESSSTARTDATE date = null ,@GENDERCODE tinyint = 0 ,@ISDMINELIGIBLE bit = 0 ,@DMINELIGIBLEREASONCODEID uniqueidentifier = null ,@WEDMSCORECODEID uniqueidentifier = null ,@WEDMSCORESTARTDATE date = null ,@WEESTGIVCAPACITYCODEID uniqueidentifier = null ,@WEESTGIVCAPACITYCOMMENT nvarchar(510) = '' ,@WEESTGIVCAPACITYSTARTDATE date = null ,@UMHSDATA_LASTPROCDATE datetime = null ,@HASUMAFFILIATION bit = null ,@WEALTHSCORES_TOTALESTIMATEDASSETS nvarchar(50) = '' ,@WEALTHSCORES_RATINGID uniqueidentifier = null ) AS SET NOCOUNT ON;
DECLARE @CURRENTDATE DATETIME; DECLARE @ISGPIMPORTEDRECORD BIT=1; -- 1 is the default that the record's data is from the Grateful Patient import process where 0 is a manual imported record (i.e. file import)
BEGIN TRY SET @CURRENTDATE = GETDATE();
IF @CHANGEAGENTID IS NULL EXEC DBO.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID OUTPUT;
IF @ID IS NULL SET @ID = NEWID()
/* Set defaults */ IF @UMHSDATA_LASTPROCDATE IS NULL BEGIN /* The GP Import process will always have a date set for Last Proc Date, otherwise if a manual import * (via the file import process or manually entered via the batch editor), then last proc date will be null */ SET @ISGPIMPORTEDRECORD = 0; END
/* Set flag of whether or not the constiteunt has either an affiliation or constituency of Faculty, Staff, or Board Member with U-M or is a spouse of an individual with one of these affiliation/constituencies. */ exec dbo.USP_USR_UMHS_HASUMAFFILIATION @CONSTITUENTID=@CONSTITUENTID, @HASUMAFFILIATION=@HASUMAFFILIATION OUTPUT;
IF @HISTORICALSTARTDATE IS NULL SET @HISTORICALSTARTDATE=@CURRENTDATE; IF @PHONESTARTDATE IS NULL SET @PHONESTARTDATE=@CURRENTDATE; IF @EMAILADDRESSSTARTDATE IS NULL SET @EMAILADDRESSSTARTDATE=@CURRENTDATE;
/* * Set the Constituent LOOKUPID based on the Constituent ID */ if @CONSTITUENTID is not null begin select @CONSTITUENTLOOKUPID=LOOKUPID from dbo.CONSTITUENT c where c.ID=@CONSTITUENTID; end
INSERT INTO DBO.USR_UMHS_BATCH (ID, BATCHID, BATCHCONSTITUENTID, SEQUENCE, CONSTITUENTID, MRN, CPISEQUENCE, CONSTITUENTLOOKUPID, FIRSTNAME, MIDDLENAME, LASTNAME, SUFFIXCODEID, BIRTHDATE, ADDRESSBLOCK, CITY, STATEID, POSTCODE, ADDRESSCOUNTRYID, ADDRESSTYPECODEID, INFOSOURCECODEID, HISTORICALSTARTDATE, PHONE, PHONETYPECODEID, PHONE_COUNTRYID, PHONE_OTHER, PHONE_OTHERTYPECODEID, PHONE_OTHER_COUNTRYID, PHONESTARTDATE, EMAILADDRESS, EMAILADDRESSTYPECODEID, EMAILADDRESSSTARTDATE, GENDERCODE, ISDMINELIGIBLE, DMINELIGIBLEREASONCODEID, WEALTHSCORES_TOTALESTIMATEDASSETS, WEALTHSCORES_RATINGID, UMHSDATA_LASTPROCDATE, ISGPIMPORTEDRECORD, HASUMAFFILIATION, ADDEDBYID, CHANGEDBYID, DATEADDED, DATECHANGED) VALUES ( @ID, @BATCHID, NEWID(), @SEQUENCE, @CONSTITUENTID, LTRIM(RTRIM(@MRN)), LTRIM(RTRIM(@CPISEQUENCE)), LTRIM(RTRIM(isnull(@CONSTITUENTLOOKUPID,''))), LTRIM(RTRIM(@FIRSTNAME)), LTRIM(RTRIM(@MIDDLENAME)), LTRIM(RTRIM(@LASTNAME)), @SUFFIXCODEID, @BIRTHDATE, @ADDRESSBLOCK, LTRIM(RTRIM(@CITY)), @STATEID, LTRIM(RTRIM(@POSTCODE)), @COUNTRYID, @ADDRESSTYPECODEID, @INFOSOURCECODEID, @HISTORICALSTARTDATE, LTRIM(RTRIM(@PHONENUMBER)), @PHONETYPECODEID, @PHONE_COUNTRYID, LTRIM(RTRIM(@PHONE_OTHER)), @PHONE_OTHERTYPECODEID, @PHONE_OTHER_COUNTRYID, @PHONESTARTDATE, LTRIM(RTRIM(ISNULL(@EMAILADDRESS, N''))), @EMAILADDRESSTYPECODEID, @EMAILADDRESSSTARTDATE, @GENDERCODE, @ISDMINELIGIBLE, @DMINELIGIBLEREASONCODEID, @WEALTHSCORES_TOTALESTIMATEDASSETS, @WEALTHSCORES_RATINGID, @UMHSDATA_LASTPROCDATE, @ISGPIMPORTEDRECORD, @HASUMAFFILIATION, @CHANGEAGENTID, @CHANGEAGENTID, @CURRENTDATE, @CURRENTDATE ) END TRY BEGIN CATCH EXEC DBO.USP_RAISE_ERROR RETURN 1 END CATCH
RETURN 0
