create procedure dbo.USR_USP_DATAFORMTEMPLATE_ADD_UMHSBATCHCOMMIT_PROVIDERS ( @ID uniqueidentifier = null output ,@CHANGEAGENTID uniqueidentifier = null ,@VALIDATEONLY bit = 0 -- Batch fields ,@CONSTITUENTID uniqueidentifier = null ,@PROVIDERDRNUMBER nvarchar(50) = '' ,@UMEMPLID nvarchar(200) = '' ,@UMUNIQUENAME nvarchar(200) = '' ,@LASTNAME nvarchar(200) = '' ,@FIRSTNAME nvarchar(100) = '' ,@PHONE nvarchar(100) = '' ,@ADMITDOCTORIND bit = 0 ,@ATTENDDOCTORIND bit = 0 ,@RESIDENTDOCTORIND bit = 0 ,@CONSULTDOCTORIND bit = 0 ,@DEPARTMENTID uniqueidentifier = null ,@SUBDEPARTMENTID uniqueidentifier = null ,@UMHSDATA_LASTPROCDATE datetime = null ) as
set nocount on;
if @ID is null set @ID = newid()
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTAPPUSERID uniqueidentifier=null; select @CURRENTAPPUSERID=ID from appuser where windowsusername=SUSER_NAME();
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
begin try
/* * Create a new Constituent in DART if one doesn't exist for the Provider, which should be updated * by then next run of the DART HRMS import process. */ if @CONSTITUENTID is NULL begin exec dbo.USP_USR_UMHS_BATCH_PROVIDER_CONSTITUENT_ADD @ID=@CONSTITUENTID output ,@UMEMPLID=@UMEMPLID ,@UMUNIQUENAME=@UMUNIQUENAME ,@LASTNAME=@LASTNAME ,@FIRSTNAME=@FIRSTNAME
/* * Set Non-Alum constituency on the Provider's new consituent record */ declare @CONSTITUENCYID uniqueidentifier; declare @CONSTITUENCYCODEID_NONALUM uniqueidentifier='BA102635-D715-4B17-B902-E96501F375D9';
if not exists(select 1 from CONSTITUENCY where CONSTITUENTID=@CONSTITUENTID and CONSTITUENCYCODEID=@CONSTITUENCYCODEID_NONALUM) begin
exec dbo.USP_DATAFORMTEMPLATE_ADD_CONSTITUENCY @ID = @CONSTITUENCYID output, @CONSTITUENTID = @CONSTITUENTID, @CONSTITUENCYCODEID = @CONSTITUENCYCODEID_NONALUM, @DATEFROM = @CURRENTDATE; end end
/* * Commit sub-department if it is a sub-department of the department */ declare @MATCHEDSUBDEPARTMENTID uniqueidentifier;
set @MATCHEDSUBDEPARTMENTID = dbo.USR_UFN_UMHSBATCH_GETMATCHEDSUBDEPARTMENTID(@DEPARTMENTID, @SUBDEPARTMENTID);
/* * Get U-M ID and Unique Name on the Constituent record instead of imported from the UMHSDATA DW * due to the U-M ID and Unique Name can be inconsistent over time in the UMHSDATA DW, so using * DART as the definitive source for a Constituent's U-M ID and Unique Name. * * The purpose of saving the associated Constituent's UMEMPLID and UMUNIQUENAME is for the Constituent Compare's * Constituent merge process. The merge will move over the UMEMPLID and UMUNIQUENAME from the Source Constituent * to the Target Constituent if the Target Constiuent doesn't have values for these fields. In order to merge the * associated Provider that is linked to the Source Constituent, the merge process (as executed in the stored * procedure USR_USP_MERGETASK_MIMED) will use the UMEMPLID and UMUNIQUENAME values set on the Provider record * to see if there is a match to the Target Constiutent and if so, then reset the CONSTITUENTID on the Provider * record to that of the ID on the Target Constituent record. */ declare @CONSTITUENT_UMEMPLID nvarchar(200)=N'', @CONSTITUENT_UMUNIQUENAME nvarchar(200)=N'';
select @CONSTITUENT_UMEMPLID=a.ALTERNATELOOKUPID from constituent c inner join alternatelookupid a on a.CONSTITUENTID=c.ID inner join ALTERNATELOOKUPIDTYPECODE atc on atc.ID=a.ALTERNATELOOKUPIDTYPECODEID where c.ID=@CONSTITUENTID and atc.ID='97CE11D1-6793-47D5-9FEE-B54B90DA16DA' -- University of Michigan ID-UMID
/* * Get the U-M Unique Name for the Target Constituent that will be used to match up with the Provider record */ select @CONSTITUENT_UMUNIQUENAME=a.ALTERNATELOOKUPID from constituent c inner join alternatelookupid a on a.CONSTITUENTID=c.ID inner join ALTERNATELOOKUPIDTYPECODE atc on atc.ID=a.ALTERNATELOOKUPIDTYPECODEID where c.ID=@CONSTITUENTID and atc.ID='4073AA02-1416-4F6F-9238-9FCEFDEE6423' -- U-M Uniqname
insert into dbo.USR_UMHS_PROVIDERS ( ID ,CONSTITUENTID ,PROVIDERDRNUMBER ,ADMITDOCTORIND ,ATTENDDOCTORIND ,RESIDENTDOCTORIND ,CONSULTDOCTORIND ,PHONE ,DEPARTMENTID ,SUBDEPARTMENTID ,UMHSDATA_LASTPROCDATE ,UMEMPLID ,UMUNIQUENAME ,ADDEDBYID ,CHANGEDBYID ,DATEADDED ,DATECHANGED) values ( NEWID() ,@CONSTITUENTID ,@PROVIDERDRNUMBER ,@ADMITDOCTORIND ,@ATTENDDOCTORIND ,@RESIDENTDOCTORIND ,@CONSULTDOCTORIND ,@PHONE ,@DEPARTMENTID ,@MATCHEDSUBDEPARTMENTID ,@UMHSDATA_LASTPROCDATE ,@CONSTITUENT_UMEMPLID ,@CONSTITUENT_UMUNIQUENAME ,@CHANGEAGENTID ,@CHANGEAGENTID ,@CURRENTDATE ,@CURRENTDATE)
end try
begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0
