create procedure dbo.USR_USP_DATAFORMTEMPLATE_ADD_UMHSBATCHCOMMIT ( @ID uniqueidentifier = null output ,@BATCHCONSTITUENTID UNIQUEIDENTIFIER = NULL ,@CHANGEAGENTID uniqueidentifier ,@VALIDATEONLY bit = 0 -- Batch fields ,@MRN nvarchar(50) -- required ,@CPISEQUENCE nvarchar(50) -- required ,@CONSTITUENTLOOKUPID nvarchar(200) = '' ,@FIRSTNAME nvarchar(50) = '' ,@MIDDLENAME nvarchar(50) = '' ,@LASTNAME nvarchar(100) = '' ,@SUFFIXCODEID uniqueidentifier = null ,@BIRTHDATE udt_fuzzydate = '00000000' -- required ,@ADDRESSBLOCK nvarchar(500) = '' ,@CITY nvarchar(100) = '' ,@STATEID uniqueidentifier = null ,@POSTCODE nvarchar(24) = '' ,@COUNTRYID uniqueidentifier = null ,@ADDRESSTYPECODEID uniqueidentifier = null ,@INFOSOURCECODEID uniqueidentifier = null ,@HISTORICALSTARTDATE date = null ,@PHONENUMBER nvarchar(200) = '' ,@PHONETYPECODEID uniqueidentifier = null ,@PHONE_COUNTRYID uniqueidentifier = null ,@PHONE_OTHER nvarchar(200) = '' ,@PHONE_OTHERTYPECODEID uniqueidentifier = null ,@PHONE_OTHER_COUNTRYID uniqueidentifier = null ,@PHONESTARTDATE date = null ,@EMAILADDRESS nvarchar(100) = '' ,@EMAILADDRESSTYPECODEID uniqueidentifier = null ,@EMAILADDRESSSTARTDATE date = null ,@GENDERCODE tinyint = 0 ,@ISDMINELIGIBLE bit = 0 ,@DMINELIGIBLEREASONCODEID uniqueidentifier = null ,@WEDMSCORECODEID uniqueidentifier = null ,@WEDMSCORESTARTDATE date = null ,@WEESTGIVCAPACITYCODEID uniqueidentifier = null ,@WEESTGIVCAPACITYCOMMENT nvarchar(510) = '' ,@WEESTGIVCAPACITYSTARTDATE date = null ,@UMHSDATA_LASTPROCDATE datetime = null ,@HASUMAFFILIATION bit = null ,@WEALTHSCORES_TOTALESTIMATEDASSETS nvarchar(50) = '' ,@WEALTHSCORES_RATINGID uniqueidentifier = null ) as
set nocount on;
/* * Creating a new patient and shell constituent record. */
declare @CURRENTDATE datetime; declare @CURRENTAPPUSERID uniqueidentifier=null; declare @CONSTITUENTID uniqueidentifier; declare @UMHSFRIENDKEYNAME nvarchar(50);
begin try
declare @STARTDATE datetime;
set @STARTDATE=case when @UMHSDATA_LASTPROCDATE is NULL then GETDATE() else @UMHSDATA_LASTPROCDATE end;
set @CURRENTDATE = getdate();
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;
select @CURRENTAPPUSERID=ID from appuser where windowsusername=SUSER_NAME();
if @ID is null set @ID = newid()
/* Get KEYNAME value for constituent shell record */ exec dbo.USR_USP_UMHS_GETUMHSFRIENDKEYNAME @CONSTITUENTID=@CONSTITUENTID output, @UMHSFRIENDKEYNAME=@UMHSFRIENDKEYNAME output;
/* Add new Constituent Shell record */ exec dbo.USP_DATAFORMTEMPLATE_ADD_INDIVIDUAL @ID=@CONSTITUENTID OUTPUT, @LASTNAME=@UMHSFRIENDKEYNAME, @CURRENTAPPUSERID=@CURRENTAPPUSERID;
/* * Set the shell record as inactive if individual is ineligible for Direct Mail and deceased */ if (1 = @ISDMINELIGIBLE) and (@DMINELIGIBLEREASONCODEID is NOT null) begin update CONSTITUENT set ISINACTIVE=1 where ID=@CONSTITUENTID; end
/* * Set Non-Alum constituency on the MIMED Shell record */ declare @CONSTITUENCYID uniqueidentifier; declare @CONSTITUENCYCODEID_NONALUM uniqueidentifier='BA102635-D715-4B17-B902-E96501F375D9'; if not exists(select 1 from CONSTITUENCY where CONSTITUENTID=@CONSTITUENTID and CONSTITUENCYCODEID=@CONSTITUENCYCODEID_NONALUM) begin
exec dbo.USP_DATAFORMTEMPLATE_ADD_CONSTITUENCY @ID = @CONSTITUENCYID output, @CONSTITUENTID = @CONSTITUENTID, @CONSTITUENCYCODEID = @CONSTITUENCYCODEID_NONALUM, @DATEFROM = @STARTDATE; end
/* * Validate the email address. */ if (0 < LEN(@EMAILADDRESS)) begin if 0 = [dbo].[UFN_VALIDEMAILADDRESS](@EMAILADDRESS) begin RAISERROR('Invalid email address format.', 13, 1); end end
exec dbo.USP_USR_UMHS_BATCH_ADD_INDIVIDUAL @CHANGEAGENTID = @CHANGEAGENTID ,@CONSTITUENTID = @CONSTITUENTID -- Primary record ID ,@MRN = @MRN -- required ,@CPISEQUENCE = @CPISEQUENCE -- required ,@FIRSTNAME = @FIRSTNAME ,@MIDDLENAME = @MIDDLENAME ,@LASTNAME = @LASTNAME -- required ,@SUFFIXCODEID = @SUFFIXCODEID ,@BIRTHDATE = @BIRTHDATE -- required ,@ADDRESSBLOCK = @ADDRESSBLOCK ,@CITY = @CITY ,@STATEID = @STATEID ,@POSTCODE = @POSTCODE ,@ADDRESSCOUNTRYID = @COUNTRYID -- United States ,@ADDRESSTYPECODEID = @ADDRESSTYPECODEID -- Home ,@INFOSOURCECODEID = @INFOSOURCECODEID -- Med/Hospital Batch ,@HISTORICALSTARTDATE = @HISTORICALSTARTDATE ,@PHONE = @PHONENUMBER ,@PHONETYPECODEID = @PHONETYPECODEID -- Home ,@PHONE_COUNTRYID = @PHONE_COUNTRYID -- United States ,@PHONE_OTHER = @PHONE_OTHER ,@PHONE_OTHERTYPECODEID = @PHONE_OTHERTYPECODEID -- Other ,@PHONE_OTHER_COUNTRYID = @PHONE_OTHER_COUNTRYID -- United States ,@PHONESTARTDATE = @PHONESTARTDATE ,@EMAILADDRESS = @EMAILADDRESS ,@EMAILADDRESSTYPECODEID = @EMAILADDRESSTYPECODEID -- Home E-mail Address ,@EMAILADDRESSSTARTDATE = @EMAILADDRESSSTARTDATE ,@ISDMINELIGIBLE = @ISDMINELIGIBLE ,@DMINELIGIBLEREASONCODEID = @DMINELIGIBLEREASONCODEID ,@GENDERCODE = @GENDERCODE ,@UMHSDATA_LASTPROCDATE = @UMHSDATA_LASTPROCDATE
declare @WEALTHSCORESATTRIBUTEID uniqueidentifier;
if @WEALTHSCORES_RATINGID is NOT NULL begin /* * Add 'Blackbaud Wealth Point Rating' */ set @WEALTHSCORESATTRIBUTEID = newid();
if @WEALTHSCORES_RATINGID is NOT NULL begin exec dbo.[USP_ATTRIBUTEC35DC7056236482E810402B9C71B3318_ADD] @ID=@WEALTHSCORESATTRIBUTEID, @PARENTID=@CONSTITUENTID, @VALUE=@WEALTHSCORES_RATINGID, @CHANGEAGENTID=@CHANGEAGENTID, @STARTDATE=@STARTDATE, @ENDDATE=default end
/* * Default the Total Estimated Assets value when empty */ if nullif(isnull(@WEALTHSCORES_TOTALESTIMATEDASSETS, ''), '') IS NULL begin set @WEALTHSCORES_TOTALESTIMATEDASSETS = N'Not available' end
/* * Add 'Blackbaud Wealth Total Estimated Assets' */ set @WEALTHSCORESATTRIBUTEID = newid();
if nullif(isnull(@WEALTHSCORES_TOTALESTIMATEDASSETS, ''), '') IS NOT NULL begin exec dbo.USP_ATTRIBUTE7EAF9B3B191F4A29A9BD7A7F63EE3F76_ADD @ID = @WEALTHSCORESATTRIBUTEID, @PARENTID = @CONSTITUENTID, @VALUE = @WEALTHSCORES_TOTALESTIMATEDASSETS, @CHANGEAGENTID=@CHANGEAGENTID, @STARTDATE = @STARTDATE, @ENDDATE=default end end
end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0
