create procedure dbo.USR_UMHS_DATAFORMTEMPLATE_EDIT_BATCH_PROVIDERS ( @ID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @PROVIDERID uniqueidentifier, @CONSTITUENTID uniqueidentifier ) as
set nocount on;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
begin try -- handle updating the data update dbo.USR_UMHS_BATCH_PROVIDERS set PROVIDERID = @PROVIDERID, CONSTITUENTID = @CONSTITUENTID, CHANGEDBYID = @CHANGEAGENTID, DATECHANGED = @CURRENTDATE where ID = @ID end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
