create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDITLOAD_UMHSBATCH_PROVIDERS ( @ID uniqueidentifier ,@DATALOADED bit = 0 output ,@TSLONG bigint = 0 output ,@SEQUENCE int = null output -- Batch fields ,@PROVIDERID uniqueidentifier = null output -- Primary Record ID ,@CONSTITUENTID uniqueidentifier = null output ,@PROVIDERDRNUMBER nvarchar(50) = null output ,@UMEMPLID nvarchar(200) = null output ,@UMUNIQUENAME nvarchar(200) = null output ,@LASTNAME nvarchar(200) = null output ,@FIRSTNAME nvarchar(100) = null output ,@PHONE nvarchar(100) = null output ,@ADMITDOCTORIND bit = null output ,@ATTENDDOCTORIND bit = null output ,@RESIDENTDOCTORIND bit = null output ,@CONSULTDOCTORIND bit = null output ,@DEPARTMENTID uniqueidentifier = null output ,@SUBDEPARTMENTID uniqueidentifier = null output ,@UMHSDATA_LASTPROCDATE datetime = null output ) as
set nocount on;
set @DATALOADED = 0 set @TSLONG = 0
select @DATALOADED = 1 ,@TSLONG = TSLONG ,@SEQUENCE = SEQUENCE ,@PROVIDERID = PROVIDERID ,@CONSTITUENTID = CONSTITUENTID ,@PROVIDERDRNUMBER = PROVIDERDRNUMBER ,@UMEMPLID = UMEMPLID ,@UMUNIQUENAME = UMUNIQUENAME ,@LASTNAME = LASTNAME ,@FIRSTNAME = FIRSTNAME ,@PHONE = PHONE ,@ADMITDOCTORIND = ADMITDOCTORIND ,@ATTENDDOCTORIND = ATTENDDOCTORIND ,@RESIDENTDOCTORIND = RESIDENTDOCTORIND ,@CONSULTDOCTORIND = CONSULTDOCTORIND ,@DEPARTMENTID = DEPARTMENTID ,@SUBDEPARTMENTID = SUBDEPARTMENTID ,@UMHSDATA_LASTPROCDATE = UMHSDATA_LASTPROCDATE from dbo.USR_UMHS_BATCH_PROVIDERS where ID = @ID
return 0;
