create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDITLOAD_UMHSBATCH_VISITS ( @ID uniqueidentifier ,@DATALOADED bit = 0 output ,@TSLONG bigint = 0 output ,@SEQUENCE int = null output -- Batch fields ,@MRN nvarchar(50) = null output ,@PROVIDERDRNUMBER nvarchar(50) = null output ,@LASTPROCDATE datetime = null output ,@VISITTYPE nvarchar(1) = null output ,@UMHSDATA_CSN bigint = null output ,@UMHSDATA_PROV_ID bigint = null output ,@VISITSTARTDATE datetime = null output ,@VISITENDDATE datetime = null output ,@PROVIDERROLE nvarchar(50) = null output ,@PATIENTHOSPITALROOM nvarchar(150) = null output ,@HOSPITALVISITDATE datetime = null output ,@DEPARTMENTID uniqueidentifier = null output ,@SUBDEPARTMENTID uniqueidentifier = null output ,@CATEGORYMEMBERDESCRIPTION nvarchar(500) = null output ) as
set nocount on;
-- be sure to set these, in case the select returns no rows set @DATALOADED = 0 set @TSLONG = 0
select @DATALOADED = 1 ,@SEQUENCE = SEQUENCE -- Batch fields ,@MRN = MRN ,@PROVIDERDRNUMBER = PROVIDERDRNUMBER ,@LASTPROCDATE = LASTPROCDATE ,@VISITTYPE = VISITTYPE ,@UMHSDATA_CSN = UMHSDATA_CSN ,@UMHSDATA_PROV_ID = UMHSDATA_PROV_ID ,@VISITSTARTDATE = VISITSTARTDATE ,@VISITENDDATE = VISITENDDATE ,@PROVIDERROLE = PROVIDERROLE ,@PATIENTHOSPITALROOM = PATIENTHOSPITALROOM ,@HOSPITALVISITDATE = HOSPITALVISITDATE ,@DEPARTMENTID = DEPARTMENTID ,@SUBDEPARTMENTID = SUBDEPARTMENTID ,@CATEGORYMEMBERDESCRIPTION = CATEGORYMEMBERDESCRIPTION from dbo.USR_UMHS_BATCH_VISITS where ID = @ID
return 0;

SaveImplementation.c:CreateProcedureSQL:
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDIT_UMHSBATCH_VISITS ( @ID uniqueidentifier ,@CHANGEAGENTID uniqueidentifier ,@SEQUENCE int -- Batch fields ,@MRN nvarchar(50) ,@PROVIDERDRNUMBER nvarchar(50) ,@LASTPROCDATE datetime ,@VISITTYPE nvarchar(1) ,@UMHSDATA_CSN bigint ,@UMHSDATA_PROV_ID bigint ,@VISITSTARTDATE datetime ,@VISITENDDATE datetime ,@PROVIDERROLE nvarchar(50) ,@PATIENTHOSPITALROOM nvarchar(150) ,@HOSPITALVISITDATE datetime ,@DEPARTMENTID uniqueidentifier ,@SUBDEPARTMENTID uniqueidentifier ,@CATEGORYMEMBERDESCRIPTION nvarchar(500) ) as
set nocount on;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
begin try -- handle updating the data update dbo.USR_UMHS_BATCH_VISITS set SEQUENCE = @SEQUENCE ,MRN = @MRN ,PROVIDERDRNUMBER = @PROVIDERDRNUMBER ,LASTPROCDATE = @LASTPROCDATE ,VISITTYPE = @VISITTYPE ,UMHSDATA_CSN = @UMHSDATA_CSN ,UMHSDATA_PROV_ID = @UMHSDATA_PROV_ID ,VISITSTARTDATE = @VISITSTARTDATE ,VISITENDDATE = @VISITENDDATE ,PROVIDERROLE = @PROVIDERROLE ,PATIENTHOSPITALROOM = @PATIENTHOSPITALROOM ,HOSPITALVISITDATE = @HOSPITALVISITDATE ,DEPARTMENTID = @DEPARTMENTID ,SUBDEPARTMENTID = @SUBDEPARTMENTID ,CATEGORYMEMBERDESCRIPTION = @CATEGORYMEMBERDESCRIPTION ,CHANGEDBYID = @CHANGEAGENTID ,DATECHANGED = @CURRENTDATE where ID = @ID end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
