create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDITLOAD_UMHSBATCH ( @ID uniqueidentifier ,@BATCHCONSTITUENTID UNIQUEIDENTIFIER = NULL OUTPUT ,@DATALOADED bit = 0 output ,@TSLONG bigint = 0 output ,@SEQUENCE int = null output -- Batch fields ,@CONSTITUENTID uniqueidentifier = null output -- Primary Record ID ,@MRN nvarchar(50) = null output ,@CPISEQUENCE nvarchar(50) = null output ,@CONSTITUENTLOOKUPID nvarchar(200) = null output ,@FIRSTNAME nvarchar(50) = null output ,@MIDDLENAME nvarchar(50) = null output ,@LASTNAME nvarchar(100) = null output ,@ISUMAFFILATION bit = null output ,@SUFFIXCODEID uniqueidentifier = null output ,@BIRTHDATE udt_fuzzydate = null output ,@ADDRESSBLOCK nvarchar(500) = null output ,@CITY nvarchar(100) = null output ,@STATEID uniqueidentifier = null output ,@POSTCODE nvarchar(24) = null output ,@COUNTRYID uniqueidentifier = null output ,@ADDRESSTYPECODEID uniqueidentifier = null output ,@INFOSOURCECODEID uniqueidentifier = null output ,@HISTORICALSTARTDATE date = null output ,@PHONENUMBER nvarchar(200) = null output ,@PHONETYPECODEID uniqueidentifier = null output ,@PHONE_COUNTRYID uniqueidentifier = null output ,@PHONE_OTHER nvarchar(200) = null output ,@PHONE_OTHERTYPECODEID uniqueidentifier = null output ,@PHONE_OTHER_COUNTRYID uniqueidentifier = null output ,@PHONESTARTDATE date = null output ,@EMAILADDRESS nvarchar(100) = null output ,@EMAILADDRESSTYPECODEID uniqueidentifier = null output ,@EMAILADDRESSSTARTDATE date = null output ,@GENDERCODE tinyint = null output ,@ISDMINELIGIBLE bit = null output ,@DMINELIGIBLEREASONCODEID uniqueidentifier = null output ,@WEDMSCORECODEID uniqueidentifier = null output ,@WEDMSCORESTARTDATE date = null output ,@WEESTGIVCAPACITYCODEID uniqueidentifier = null output ,@WEESTGIVCAPACITYCOMMENT nvarchar(510) = null output ,@WEESTGIVCAPACITYSTARTDATE date = null output ,@UMHSDATA_LASTPROCDATE datetime = null output ,@ISGPIMPORTEDRECORD bit = null output ,@HASUMAFFILIATION bit = null output ,@WEALTHSCORES_TOTALESTIMATEDASSETS nvarchar(50) = null output ,@WEALTHSCORES_RATINGID uniqueidentifier = null output ) as
set nocount on;
-- be sure to set these, in case the select returns no rows set @DATALOADED = 0 set @TSLONG = 0
-- populate the output parameters, which correspond to fields on the form. Note that -- we set @DATALOADED = 1 to indicate that the load was successful. Otherwise, the system -- will display a "no data loaded" message. Also note that we fetch the TSLONG so that concurrency -- can be considered. select @DATALOADED = 1 ,@TSLONG = b.TSLONG ,@BATCHCONSTITUENTID = B.BATCHCONSTITUENTID ,@CONSTITUENTID = b.CONSTITUENTID ,@SEQUENCE = b.SEQUENCE ,@MRN = b.MRN ,@CPISEQUENCE= b.CPISEQUENCE ,@CONSTITUENTLOOKUPID = c.LOOKUPID ,@FIRSTNAME = b.FIRSTNAME ,@MIDDLENAME = b.MIDDLENAME ,@LASTNAME = b.LASTNAME ,@SUFFIXCODEID = b.SUFFIXCODEID ,@BIRTHDATE = b.BIRTHDATE ,@ADDRESSBLOCK = b.ADDRESSBLOCK ,@CITY = b.CITY ,@STATEID = b.STATEID ,@POSTCODE = b.POSTCODE ,@COUNTRYID = b.ADDRESSCOUNTRYID ,@ADDRESSTYPECODEID = b.ADDRESSTYPECODEID ,@INFOSOURCECODEID = b.INFOSOURCECODEID ,@HISTORICALSTARTDATE = b.HISTORICALSTARTDATE ,@PHONENUMBER = b.PHONE ,@PHONETYPECODEID = b.PHONETYPECODEID ,@PHONE_COUNTRYID = b.PHONE_COUNTRYID ,@PHONE_OTHER = b.PHONE_OTHER ,@PHONE_OTHERTYPECODEID = b.PHONE_OTHERTYPECODEID ,@PHONE_OTHER_COUNTRYID = b.PHONE_OTHER_COUNTRYID ,@PHONESTARTDATE = b.PHONESTARTDATE ,@EMAILADDRESS = b.EMAILADDRESS ,@EMAILADDRESSTYPECODEID = b.EMAILADDRESSTYPECODEID ,@EMAILADDRESSSTARTDATE = b.EMAILADDRESSSTARTDATE ,@GENDERCODE = b.GENDERCODE ,@ISDMINELIGIBLE = b.ISDMINELIGIBLE ,@DMINELIGIBLEREASONCODEID = b.DMINELIGIBLEREASONCODEID ,@WEALTHSCORES_TOTALESTIMATEDASSETS = b.WEALTHSCORES_TOTALESTIMATEDASSETS ,@WEALTHSCORES_RATINGID = b.WEALTHSCORES_RATINGID ,@UMHSDATA_LASTPROCDATE = b.UMHSDATA_LASTPROCDATE ,@ISGPIMPORTEDRECORD = b.ISGPIMPORTEDRECORD ,@HASUMAFFILIATION = b.HASUMAFFILIATION from dbo.USR_UMHS_BATCH b left outer join dbo.CONSTITUENT c on c.ID=b.CONSTITUENTID where b.ID = @ID
set @ISUMAFFILATION=@HASUMAFFILIATION;
return 0;

SaveImplementation.c:CreateProcedureSQL:
create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDIT_UMHSBATCH ( @ID uniqueidentifier ,@BATCHCONSTITUENTID UNIQUEIDENTIFIER ,@CHANGEAGENTID uniqueidentifier ,@SEQUENCE int -- Batch fields ,@CONSTITUENTID uniqueidentifier -- Primary Record ID ,@MRN nvarchar(50) ,@CPISEQUENCE nvarchar(50) ,@CONSTITUENTLOOKUPID nvarchar(200) ,@FIRSTNAME nvarchar(50) ,@MIDDLENAME nvarchar(50) ,@LASTNAME nvarchar(100) ,@SUFFIXCODEID uniqueidentifier ,@BIRTHDATE udt_fuzzydate ,@ADDRESSBLOCK nvarchar(500) ,@CITY nvarchar(100) ,@STATEID uniqueidentifier ,@POSTCODE nvarchar(24) ,@COUNTRYID uniqueidentifier ,@ADDRESSTYPECODEID uniqueidentifier ,@INFOSOURCECODEID uniqueidentifier ,@HISTORICALSTARTDATE date ,@PHONENUMBER nvarchar(200) ,@PHONETYPECODEID uniqueidentifier ,@PHONE_COUNTRYID uniqueidentifier ,@PHONE_OTHER nvarchar(200) ,@PHONE_OTHERTYPECODEID uniqueidentifier ,@PHONE_OTHER_COUNTRYID uniqueidentifier ,@PHONESTARTDATE date ,@EMAILADDRESS nvarchar(100) ,@EMAILADDRESSTYPECODEID uniqueidentifier ,@EMAILADDRESSSTARTDATE date ,@GENDERCODE tinyint ,@ISDMINELIGIBLE bit ,@DMINELIGIBLEREASONCODEID uniqueidentifier ,@WEDMSCORECODEID uniqueidentifier ,@WEDMSCORESTARTDATE date ,@WEESTGIVCAPACITYCODEID uniqueidentifier ,@WEESTGIVCAPACITYCOMMENT nvarchar(510) ,@WEESTGIVCAPACITYSTARTDATE date ,@UMHSDATA_LASTPROCDATE datetime ,@HASUMAFFILIATION bit ,@WEALTHSCORES_TOTALESTIMATEDASSETS nvarchar(50) ,@WEALTHSCORES_RATINGID uniqueidentifier ) as
set nocount on;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output
declare @CURRENTDATE datetime set @CURRENTDATE = getdate()
begin try
/* Get flag to indication constituent's affiliation to U-M Faculty, Staff or Board Member or spouse of a U-M Faculty, Staff or Board Member */ exec dbo.USP_USR_UMHS_HASUMAFFILIATION @CONSTITUENTID=@CONSTITUENTID, @HASUMAFFILIATION=@HASUMAFFILIATION OUTPUT;
update dbo.USR_UMHS_BATCH set SEQUENCE = @SEQUENCE ,BATCHCONSTITUENTID = @BATCHCONSTITUENTID ,CHANGEDBYID = @CHANGEAGENTID ,DATECHANGED = @CURRENTDATE ,CONSTITUENTID = @CONSTITUENTID ,MRN = LTRIM(RTRIM(@MRN)) ,CPISEQUENCE = LTRIM(RTRIM(@CPISEQUENCE)) ,FIRSTNAME = LTRIM(RTRIM(@FIRSTNAME)) ,MIDDLENAME = LTRIM(RTRIM(@MIDDLENAME)) ,LASTNAME = LTRIM(RTRIM(@LASTNAME)) ,SUFFIXCODEID = @SUFFIXCODEID ,BIRTHDATE = @BIRTHDATE ,ADDRESSBLOCK = @ADDRESSBLOCK ,CITY = @CITY ,STATEID = @STATEID ,POSTCODE = @POSTCODE ,ADDRESSCOUNTRYID = @COUNTRYID ,ADDRESSTYPECODEID = @ADDRESSTYPECODEID ,INFOSOURCECODEID = @INFOSOURCECODEID ,HISTORICALSTARTDATE = @HISTORICALSTARTDATE ,PHONE = @PHONENUMBER ,PHONETYPECODEID = @PHONETYPECODEID ,PHONE_COUNTRYID = @PHONE_COUNTRYID ,PHONE_OTHER = @PHONE_OTHER ,PHONE_OTHERTYPECODEID = @PHONE_OTHERTYPECODEID ,PHONE_OTHER_COUNTRYID = @PHONE_OTHER_COUNTRYID ,PHONESTARTDATE = @PHONESTARTDATE ,EMAILADDRESS = LTRIM(RTRIM(ISNULL(@EMAILADDRESS, N''))) ,EMAILADDRESSTYPECODEID = @EMAILADDRESSTYPECODEID ,EMAILADDRESSSTARTDATE = @EMAILADDRESSSTARTDATE ,GENDERCODE = @GENDERCODE ,ISDMINELIGIBLE = @ISDMINELIGIBLE ,DMINELIGIBLEREASONCODEID = @DMINELIGIBLEREASONCODEID ,WEALTHSCORES_TOTALESTIMATEDASSETS = @WEALTHSCORES_TOTALESTIMATEDASSETS ,WEALTHSCORES_RATINGID = @WEALTHSCORES_RATINGID ,HASUMAFFILIATION = @HASUMAFFILIATION where ID = @ID
end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
