SQLFunctionSpec.CreateFunctionSQL:
create function dbo.USR_UFN_UMHSBATCH_GETMATCHEDSUBDEPARTMENTID(@DEPARTMENTID uniqueidentifier, @SUBDEPARTMENTID uniqueidentifier) returns uniqueidentifier with execute as caller as begin declare @MATCHEDSUBDEPARTMENTID uniqueidentifier=null;
select @MATCHEDSUBDEPARTMENTID=ID from dbo.USR_UMHS_SUBDEPARTMENT where ID=@SUBDEPARTMENTID and DEPARTMENTID=@DEPARTMENTID
return @MATCHEDSUBDEPARTMENTID end
