create function dbo.USR_UFN_TRANSLATIONFUNCTION_UMSH_BATCH_PROVIDERNAME
(
	@ID uniqueidentifier
) 
returns nvarchar(200) 
as
begin
	declare @NAME nvarchar(200);

  select 
    @NAME=c.NAME 
  from dbo.USR_UMHS_PROVIDERS p
  inner join dbo.CONSTITUENT c on c.ID=p.CONSTITUENTID
  where 
    p.ID=@ID

	return @NAME;
end
