CREATE PROCEDURE DBO.USR_UM_USP_DATAFORMTEMPLATE_EDIT_UMHSREFERRALBATCH (@ID UNIQUEIDENTIFIER, @REFERRALID UNIQUEIDENTIFIER, @UMHSID UNIQUEIDENTIFIER, @SEQUENCE INT, @CHANGEAGENTID UNIQUEIDENTIFIER = NULL, @REFERRERID UNIQUEIDENTIFIER, @REFERRERROLECODEID UNIQUEIDENTIFIER, @DEPARTMENTID UNIQUEIDENTIFIER, @SUBDEPARTMENTID UNIQUEIDENTIFIER, @FOLLOWUPCOMPLETED bit, @FOLLOWUPSTATUSCODE tinyint, @REFERRALDATE DATETIME, @UMHSDEVSTAFFID UNIQUEIDENTIFIER, @COMMENTS NVARCHAR(MAX)) AS SET NOCOUNT ON;
IF @CHANGEAGENTID IS NULL EXEC DBO.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID OUTPUT
BEGIN TRY -- handle updating the data UPDATE DBO.USR_UMHS_REFERRALBATCH SET REFERRALID = @REFERRALID, UMHSID = @UMHSID, REFERRERID = @REFERRERID, REFERRERROLECODEID = @REFERRERROLECODEID, DEPARTMENTID = @DEPARTMENTID, SUBDEPARTMENTID = @SUBDEPARTMENTID, FOLLOWUPCOMPLETED = @FOLLOWUPCOMPLETED, FOLLOWUPSTATUSCODE = @FOLLOWUPSTATUSCODE, REFERRALDATE = @REFERRALDATE, UMHSDEVSTAFFID = @UMHSDEVSTAFFID, COMMENTS = @COMMENTS, CHANGEDBYID = @CHANGEAGENTID, DATECHANGED = GETDATE() WHERE ID = @ID END TRY
BEGIN CATCH EXEC DBO.USP_RAISE_ERROR
RETURN 1 END CATCH
RETURN 0;
