SPSearchList.c:CreateProcedureSQL:
CREATE PROCEDURE DBO.USR_USP_SEARCHLIST_FUNDRAISERBYLOOKUPID ( @LOOKUPID NVARCHAR(100) = NULL, @MAXROWS SMALLINT = 500 ) AS set @LOOKUPID = dbo.UFN_SEARCHCRITERIA_GETLIKEPARAMETERVALUE(@LOOKUPID, 0, null);
DECLARE @CURRENTDATE DATETIME = GETDATE(); DECLARE @CURRENTDATEEARLIESTTIME DATE = @CURRENTDATE;
SELECT TOP (@MAXROWS) CONSTITUENT.ID [ID], NULL [SORTCONSTITUENTNAME], ADDRESS.CITY [CITY], DBO.UFN_STATE_GETDESCRIPTION(ADDRESS.STATEID) [STATE], ADDRESS.POSTCODE [POSTCODE], PHONE.NUMBER [PHONE], CASE WHEN CONSTITUENT.ISGROUP = 0 AND CONSTITUENT.ISORGANIZATION = 0 THEN 'INDIVIDUAL' WHEN CONSTITUENT.ISORGANIZATION = 1 THEN 'ORGANIZATION' WHEN GD.GROUPTYPECODE = 0 THEN 'HOUSEHOLD' ELSE 'GROUP' END [CONSTITUENTTYPE], ( COALESCE((SELECT DBO.UDA_BUILDLIST(SITE.NAME) FROM DBO.SITE LEFT JOIN DBO.CONSTITUENTSITE ON CONSTITUENTSITE.CONSTITUENTID = CONSTITUENT.ID WHERE CONSTITUENTSITE.SITEID = SITE.ID), '') ) [SITE], NF.NAME [NAME] FROM DBO.CONSTITUENT OUTER APPLY DBO.UFN_CONSTITUENT_DISPLAYNAME(CONSTITUENT.ID) NF LEFT OUTER JOIN ADDRESS ON CONSTITUENT.ID = ADDRESS.CONSTITUENTID AND ADDRESS.ISPRIMARY = 1 LEFT OUTER JOIN PHONE ON CONSTITUENT.ID = PHONE.CONSTITUENTID AND PHONE.ISPRIMARY = 1 LEFT OUTER JOIN DBO.GROUPDATA GD ON CONSTITUENT.ID = GD.ID INNER JOIN DBO.FUNDRAISERDATERANGE ON FUNDRAISERDATERANGE.CONSTITUENTID = CONSTITUENT.ID AND ( FUNDRAISERDATERANGE.DATEFROM <= @CURRENTDATEEARLIESTTIME OR FUNDRAISERDATERANGE.DATEFROM IS NULL ) AND ( @CURRENTDATEEARLIESTTIME <= FUNDRAISERDATERANGE.DATETO OR FUNDRAISERDATERANGE.DATETO IS NULL ) WHERE CONSTITUENT.LOOKUPID LIKE @LOOKUPID AND CONSTITUENT.ISCONSTITUENT = 1 AND NOT EXISTS (SELECT TOP 1 ID FROM DBO.DECEASEDCONSTITUENT WHERE DECEASEDCONSTITUENT.ID = CONSTITUENT.ID) AND CONSTITUENT.ISORGANIZATION = 0 AND CONSTITUENT.ISGROUP = 0 AND CONSTITUENT.ISINACTIVE = 0 ORDER BY CONSTITUENT.KEYNAME, CONSTITUENT.FIRSTNAME, CONSTITUENT.MIDDLENAME;
