SPSearchList.c:CreateProcedureSQL:
create procedure dbo.USR_USP_SEARCHLIST_UMHS_BATCH_PROVIDER_CONSTITUENTID ( @UMEMPLID nvarchar(200) ,@UMUNIQUENAME nvarchar(200) ,@PROVIDERDRNUMBER nvarchar(50) ,@MAXROWS smallint = 500 ) as declare @CONSTITUENTID uniqueidentifier=NULL;
select @CONSTITUENTID=CONSTITUENTID from dbo.USR_UMHS_PROVIDERS where PROVIDERDRNUMBER=@PROVIDERDRNUMBER
if @CONSTITUENTID is NULL begin select @CONSTITUENTID=c.ID from constituent c inner join alternatelookupid a on a.CONSTITUENTID=c.ID inner join ALTERNATELOOKUPIDTYPECODE atc on atc.ID=a.ALTERNATELOOKUPIDTYPECODEID where a.ALTERNATELOOKUPID=@UMEMPLID and atc.ID='97CE11D1-6793-47D5-9FEE-B54B90DA16DA' -- University of Michigan ID-UMID end
if @CONSTITUENTID is NULL begin select @CONSTITUENTID=c.ID from constituent c inner join alternatelookupid a on a.CONSTITUENTID=c.ID inner join ALTERNATELOOKUPIDTYPECODE atc on atc.ID=a.ALTERNATELOOKUPIDTYPECODEID where a.ALTERNATELOOKUPID=@UMUNIQUENAME and atc.ID='4073AA02-1416-4F6F-9238-9FCEFDEE6423' -- U-M Uniqname end
select @CONSTITUENTID as ID;
