CREATE FUNCTION DBO.USR_UFN_TRANSLATIONFUNCTION_UMHSDATA_SEARCH (@ID UNIQUEIDENTIFIER) RETURNS NVARCHAR(50) AS BEGIN DECLARE @S NVARCHAR(50);
IF EXISTS(SELECT ID FROM DBO.USR_UMHS_DATA D WHERE ID = @ID) SELECT @S = CONCAT(D.FIRSTNAME, ' ', D.KEYNAME) FROM DBO.USR_UMHS_DATA D WHERE D.ID = @ID ELSE SELECT @S = CONCAT(C.FIRSTNAME, ' ', C.KEYNAME) FROM DBO.CONSTITUENT C WHERE ID = @ID
RETURN @S; END
