create procedure dbo.USR_UM_USP_DATAFORMTEMPLATE_EDITLOAD_UMHSDEPARTMENT
(
    @ID uniqueidentifier,
                    @DATALOADED bit = 0 output,
                    @TSLONG bigint = 0 output,
                    @NAME nvarchar(100) = null output,
                    @SORTMETHODCODE tinyint = null output,
                    @ISINACTIVE bit = null output
                )
                as
                    set nocount on;

                    set @DATALOADED = 0;
                    set @TSLONG = 0;

                    select
                        @DATALOADED = 1,
                        @NAME = D.NAME,
                        @ISINACTIVE = D.ISINACTIVE,
                        @TSLONG = D.TSLONG,
                        @SORTMETHODCODE = SORTMETHODCODE
                    from
                        dbo.USR_UMHS_DEPARTMENT D
                    where
                        D.ID = @ID;
	return 0;

SaveImplementation.c:CreateProcedureSQL:

create procedure dbo.USR_UM_USP_DATAFORMTEMPLATE_EDIT_UMHSDEPARTMENT 
(
                    @ID uniqueidentifier,
                    @CHANGEAGENTID uniqueidentifier = null,
                    @NAME nvarchar(100),
                    @SORTMETHODCODE tinyint,
                    @ISINACTIVE bit
                )
                as
                    set nocount on;

                    declare @CURRENTDATE datetime;

                    if @CHANGEAGENTID is null  
                        exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;

                    set @CURRENTDATE = getdate();

                    begin try
                        update
                            dbo.USR_UMHS_DEPARTMENT
                        set
                            NAME = @NAME,
                            ISINACTIVE = @ISINACTIVE,
                            DATECHANGED = @CURRENTDATE,
                            CHANGEDBYID = @CHANGEAGENTID,
                            SORTMETHODCODE = @SORTMETHODCODE
                        where 
                            ID = @ID;

                    end try
                    begin catch
                        exec dbo.USP_RAISE_ERROR;
                        return 1;
                    end catch

return 0;
