SPOperationImplementation.c:CreateProcedureSQL:

          create procedure dbo.USR_UM_USP_RECORDOPERATION_UMHSDEPARTMENTSETINACTIVE
         (
					@ID uniqueidentifier,
					@CHANGEAGENTID uniqueidentifier = null
				)
				as
					set nocount on;

					if @CHANGEAGENTID is null  
						exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;

					update
						dbo.USR_UMHS_DEPARTMENT
					set
						ISINACTIVE = 1,
						CHANGEDBYID = @CHANGEAGENTID,
						DATECHANGED = getdate()
					where
						USR_UMHS_DEPARTMENT.ID = @ID;
	          return 0;

Provider.c:CreateProcedureSQL:

create procedure dbo.USR_UM_USP_RECORDOPERATION_UMHSDEPARTMENTMARKINACTIVEPROMPT
(
    @ID uniqueidentifier,
    @NAME nvarchar(100) output
)
as
set nocount on;

select
  @NAME = NAME
from
  dbo.USR_UMHS_DEPARTMENT
where
  ID = @ID;

  return 0;
