create procedure dbo.USR_UM_USP_DATAFORMTEMPLATE_ADD_UMHSSUBDEPARTMENT
  (
                        @ID uniqueidentifier = null output,
                        @CHANGEAGENTID uniqueidentifier = null, 
                        @DEPARTMENTID uniqueidentifier,
                        @NAME nvarchar(100)
                    )
                    as
                        set nocount on;

                        declare @CURRENTDATE datetime;
                        declare @SEQUENCE int;

                        if @ID is null
                            set @ID = newid();

                        if @CHANGEAGENTID is null  
                            exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;

                        set @CURRENTDATE = getdate();

                        set @SEQUENCE = (SELECT isnull(max(SEQUENCE)+1,0) from dbo.USR_UMHS_SUBDEPARTMENT where DEPARTMENTID = @DEPARTMENTID);

                        begin try
                            insert into dbo.USR_UMHS_SUBDEPARTMENT
                            (
                                ID,
                                DEPARTMENTID,
                                NAME,
                                SEQUENCE,
                                ADDEDBYID,
                                CHANGEDBYID,
                                DATEADDED,
                                DATECHANGED
                            )
                            values
                            (
                                @ID,
                                @DEPARTMENTID,
                                @NAME,
                                @SEQUENCE,
                                @CHANGEAGENTID,
                                @CHANGEAGENTID,
                                @CURRENTDATE,
                                @CURRENTDATE
                            );

                        end try

                        begin catch
                            exec dbo.USP_RAISE_ERROR;
                            return 1;
                        end catch
	          return 0;	

return 0
