create procedure dbo.USR_UM_USP_DATAFORMTEMPLATE_EDITLOAD_UMHSSUBDEPARTMENT
(
    @ID uniqueidentifier,
    @DATALOADED bit = 0 output,
    @TSLONG bigint = 0 output,
    @NAME nvarchar(100) = null output,
    @ISINACTIVE bit = null output
)
as
    set nocount on;

    set @DATALOADED = 0;
    set @TSLONG = 0;

    select
        @DATALOADED = 1,
        @NAME = NAME,
        @ISINACTIVE = ISINACTIVE,
        @TSLONG = TSLONG
    from
        dbo.USR_UMHS_SUBDEPARTMENT
    where
        ID = @ID;

    return 0;

SaveImplementation.c:CreateProcedureSQL:

            create procedure dbo.USR_UM_USP_DATAFORMTEMPLATE_EDIT_UMHSSUBDEPARTMENT 
            (
	            @ID uniqueidentifier,
                    @CHANGEAGENTID uniqueidentifier = null,
                    @NAME nvarchar(100),
                    @ISINACTIVE bit
                )
                as
                    set nocount on;

                    declare @CURRENTDATE datetime;

                    if @CHANGEAGENTID is null  
                        exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;

                    set @CURRENTDATE = getdate();

                    begin try
                        update
                            dbo.USR_UMHS_SUBDEPARTMENT
                        set
                            NAME = @NAME,
                            ISINACTIVE = @ISINACTIVE,
                            DATECHANGED = @CURRENTDATE,
                            CHANGEDBYID = @CHANGEAGENTID
                        where 
                            ID = @ID;

                    end try
                    begin catch
                        exec dbo.USP_RAISE_ERROR;
                        return 1;
                    end catch

                    return 0;
