create procedure dbo.USR_UM_USP_RECORDOPERATION_UMHSSUBDEPARTMENTSETINACTIVE
(
          @ID uniqueidentifier,
          @CHANGEAGENTID uniqueidentifier = null
      )
      as
          set nocount on;

          if @CHANGEAGENTID is null  
              exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;

          update
              dbo.USR_UMHS_SUBDEPARTMENT
          set
              ISINACTIVE = 1,
              CHANGEDBYID = @CHANGEAGENTID,
              DATECHANGED = getdate()
          where
              ID = @ID;

          return 0;

Provider.CreateProcedureSQL:

create procedure dbo.USP_RECORDOPERATION_SUBDEPARTMENTMARKINACTIVEPROMPT
  (
    @ID uniqueidentifier,
    @NAME nvarchar(100) output
  )
    as
    set nocount on;

    select
    @NAME = NAME
    from
    dbo.USR_UMHS_SUBDEPARTMENT
    where
    ID = @ID;

  return 0;
