      create function dbo.USR_UFN_UMHSCONSTITUENT_BUILDNAME
      (
	      @ID uniqueidentifier
      ) 
      returns nvarchar(154)
			as begin
				if @ID is null
					return '';
				return coalesce((select NAME from dbo.CONSTITUENT where ID=@ID), (select KEYNAME + ', ' + FIRSTNAME from dbo.USR_UMHS_DATA where ID=@ID), '');
			end
