create procedure dbo.USR_UM_USP_DATALIST_UMHSAFFILIATIONBASE as set nocount on;
SELECT UAB.ID, UAC.DESCRIPTION AS CONSTITUENTAFFILIATIONCATEGORY, UAB.UMHSAFFILIATIONBASENAME, UAB.UMHSAFFILIATIONBASEDESCRIPTION
FROM dbo.USR_UMHS_AFFILIATIONBASE UAB JOIN dbo.USR_UMHS_AFFILIATIONCATEGORYTYPECODE UAC ON UAB.UMHSAFFILIATIONCATEGORYCODEID = UAC.ID ORDER BY UAB.UMHSAFFILIATIONBASENAME
