create procedure dbo.USR_UM_USP_MIMEDTEAMSITE (	     
        @TEAMMEMBERID uniqueidentifier = null
      ) as
      set nocount on;
 select
        a.SITEID as VALUE, s.NAME as LABEL
      from dbo.APPUSER a
	  inner join dbo.SITE s on s.ID = a.SITEID
	  WHERE a.CONSTITUENTID = @TEAMMEMBERID
        order by Name
