SQLStoredProcedureSpec.CreateProcedureSQL:
CREATE PROCEDURE DBO.USR_USP_MIMEDDEFAULTSITEFORMEMBER (@TEAMMEMBERID UNIQUEIDENTIFIER) AS BEGIN DECLARE @SITEID UNIQUEIDENTIFIER = NULL;
SELECT @SITEID = SITEID FROM DBO.ORGANIZATIONPOSITION O INNER JOIN ORGANIZATIONPOSITIONHOLDER OH ON OH.POSITIONID = O.ID WHERE OH.CONSTITUENTID = @TEAMMEMBERID
SELECT @SITEID; END
