      create function dbo.USR_UM_UFN_TRANSLATIONFUNCTION_DEPARTMENT_GETNAME
      (
	      @ID uniqueidentifier
      ) 
      returns nvarchar(50) 
      as
      begin
	      declare @name nvarchar(50);
	      select @name = name from dbo.USR_UMHS_DEPARTMENT where ID = @ID;
	      return @name;
      end
